import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useTranslation } from "react-i18next";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Navigation } from "@/components/navigation";
import { LanguageSelector } from "@/components/language-selector";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Settings as SettingsIcon, Plug, CheckCircle, XCircle, Loader2, DollarSign, AlertTriangle, RefreshCw } from "lucide-react";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";

export default function Settings() {
  const { t } = useTranslation();
  const { toast } = useToast();
  const [apiKey, setApiKey] = useState("");
  const [secret, setSecret] = useState("");
  const [testnet, setTestnet] = useState(false);
  const [showRealModeDialog, setShowRealModeDialog] = useState(false);

  // Query Binance connection status
  const { data: status } = useQuery<{ connected: boolean; message: string }>({
    queryKey: ['/api/binance/status'],
    refetchInterval: 10000,
  });

  // Query trading mode
  const { data: tradingMode } = useQuery<{ mode: string; realBalance?: number }>({
    queryKey: ['/api/trading-mode'],
    enabled: status?.connected ?? false,
  });

  // Query real balance
  const { data: realBalance, refetch: refetchBalance } = useQuery<{ total: number; assets: Record<string, number> }>({
    queryKey: ['/api/binance/balance'],
    enabled: false, // Only fetch when user clicks button
  });

  // Save credentials mutation
  const saveMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest('POST', '/api/binance/save-credentials', {
        apiKey,
        secret,
      });
      return await response.json();
    },
    onSuccess: (data: any) => {
      if (data.success) {
        toast({
          title: t('common.success'),
          description: 'API credentials saved successfully and encrypted in database',
        });
        queryClient.invalidateQueries({ queryKey: ['/api/binance/status'] });
        queryClient.invalidateQueries({ queryKey: ['/api/trading-mode'] });

        setApiKey('');
        setSecret('');
      } else {
        toast({
          title: t('common.error'),
          description: data.message || 'Failed to save credentials',
          variant: "destructive",
        });
      }
    },
    onError: (error) => {
      toast({
        title: t('common.error'),
        description: error instanceof Error ? error.message : 'Failed to save credentials',
        variant: "destructive",
      });
    },
  });

  // Test connection mutation
  const testMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest('POST', '/api/binance/test', {
        apiKey: apiKey || undefined,
        secret: secret || undefined,
        testnet,
      });
      return await response.json();
    },
    onSuccess: (data: any) => {
      if (data.success) {
        toast({
          title: t('common.success'),
          description: data.message,
        });
        queryClient.invalidateQueries({ queryKey: ['/api/binance/status'] });
        queryClient.invalidateQueries({ queryKey: ['/api/trading-mode'] });
      } else {
        toast({
          title: t('common.error'),
          description: data.message,
          variant: "destructive",
        });
      }
    },
    onError: (error) => {
      toast({
        title: t('common.error'),
        description: error instanceof Error ? error.message : t('common.testConnectionFailed'),
        variant: "destructive",
      });
    },
  });

  // Switch trading mode mutation
  const switchModeMutation = useMutation({
    mutationFn: async (mode: string) => {
      const response = await apiRequest('POST', '/api/trading-mode', {
        mode,
        confirmation: mode === 'real',
      });
      return await response.json();
    },
    onSuccess: (data: any) => {
      toast({
        title: t('common.success'),
        description: `Switched to ${data.mode} trading mode`,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/trading-mode'] });
      queryClient.invalidateQueries({ queryKey: ['/api/bot/status'] });
    },
    onError: (error: any) => {
      toast({
        title: t('common.error'),
        description: error.message || 'Failed to switch trading mode',
        variant: "destructive",
      });
    },
  });

  return (
    <div className="min-h-screen bg-background">
      <header className="sticky top-0 z-50 border-b border-border bg-background/95 backdrop-blur supports-[backdrop-filter]:bg-background/60">
        <div className="container mx-auto px-6 py-4 space-y-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <SettingsIcon className="h-6 w-6 text-primary" />
              <div>
                <h1 className="text-2xl font-bold" data-testid="text-settings-title">{t('settings.title')}</h1>
                <p className="text-sm text-muted-foreground">
                  {t('settings.subtitle')}
                </p>
              </div>
            </div>
            <LanguageSelector />
          </div>
          <Navigation />
        </div>
      </header>

      <main className="container mx-auto px-6 py-6">
        {/* Binance API Configuration */}
        <Card className="p-6 mb-6" data-testid="card-binance-config">
          <div className="flex items-center gap-2 mb-6">
            <Plug className="h-5 w-5 text-primary" />
            <h2 className="text-lg font-semibold">{t('settings.binanceConfig')}</h2>
            {status && (
              <Badge
                variant={status.connected ? "default" : "secondary"}
                className="ml-auto"
                data-testid="badge-connection-status"
              >
                {status.connected ? (
                  <><CheckCircle className="h-3 w-3 mr-1" /> {t('settings.connected')}</>
                ) : (
                  <><XCircle className="h-3 w-3 mr-1" /> {t('settings.notConnected')}</>
                )}
              </Badge>
            )}
          </div>

          <div className="space-y-4 mb-6">
            <div className="p-4 rounded-md bg-muted/30 border border-border">
              <p className="text-sm text-muted-foreground">
                <strong>{t('settings.note')}:</strong> {t('settings.binanceNote')}
              </p>
            </div>

            <div className="space-y-3">
              <div>
                <Label htmlFor="api-key">{t('settings.apiKey')}</Label>
                <Input
                  id="api-key"
                  type="password"
                  placeholder={t('settings.apiKey')}
                  value={apiKey}
                  onChange={(e) => setApiKey(e.target.value)}
                  className="mt-1"
                  data-testid="input-api-key"
                />
              </div>

              <div>
                <Label htmlFor="api-secret">{t('settings.apiSecret')}</Label>
                <Input
                  id="api-secret"
                  type="password"
                  placeholder={t('settings.apiSecret')}
                  value={secret}
                  onChange={(e) => setSecret(e.target.value)}
                  className="mt-1"
                  data-testid="input-api-secret"
                />
              </div>

              <div className="flex items-center gap-2">
                <input
                  id="testnet"
                  type="checkbox"
                  checked={testnet}
                  onChange={(e) => setTestnet(e.target.checked)}
                  className="rounded"
                  data-testid="checkbox-testnet"
                />
                <Label htmlFor="testnet" className="cursor-pointer">
                  {t('settings.testnet')}
                </Label>
              </div>
            </div>
          </div>

          <div className="flex gap-3">
            <Button
              onClick={() => testMutation.mutate()}
              disabled={testMutation.isPending || !apiKey || !secret}
              variant="outline"
              className="flex-1"
              data-testid="button-test-connection"
            >
              {testMutation.isPending ? (
                <>
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  {t('settings.testing')}
                </>
              ) : (
                <>
                  <Plug className="mr-2 h-4 w-4" />
                  {t('settings.testConnection')}
                </>
              )}
            </Button>

            <Button
              onClick={() => saveMutation.mutate()}
              disabled={saveMutation.isPending || !apiKey || !secret}
              className="flex-1"
              data-testid="button-save-credentials"
            >
              {saveMutation.isPending ? (
                <>
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  Saving...
                </>
              ) : (
                <>
                  <CheckCircle className="mr-2 h-4 w-4" />
                  Save Credentials
                </>
              )}
            </Button>
          </div>
        </Card>

        {/* Trading Mode Configuration */}
        {status?.connected && (
          <Card className="p-6 mb-6" data-testid="card-trading-mode">
            <div className="flex items-center gap-2 mb-6">
              <DollarSign className="h-5 w-5 text-primary" />
              <h2 className="text-lg font-semibold">{t('settings.tradingMode') || 'Trading Mode'}</h2>
              {tradingMode && (
                <Badge
                  variant={tradingMode.mode === 'real' ? "destructive" : "default"}
                  className="ml-auto"
                  data-testid="badge-current-mode"
                >
                  {tradingMode.mode === 'real' ? 'ðŸ”´ REAL MONEY' : 'ðŸ“ Paper Trading'}
                </Badge>
              )}
            </div>

            <div className="space-y-4">
              {/* Current Mode Info */}
              <div className="p-4 rounded-md bg-muted/30 border border-border">
                <p className="text-sm font-medium mb-2">
                  {tradingMode?.mode === 'real' 
                    ? t('settings.realModeActive') || 'ðŸ”´ Real Trading Mode Active'
                    : t('settings.paperModeActive') || 'ðŸ“ Paper Trading Mode Active'
                  }
                </p>
                <p className="text-sm text-muted-foreground">
                  {tradingMode?.mode === 'real'
                    ? t('settings.realModeDesc') || 'Your bot is trading with real money from your Binance account. All trades will execute on the live market.'
                    : t('settings.paperModeDesc') || 'Your bot is simulating trades with virtual money. No real funds are used.'}
                </p>
              </div>

              {/* Real Balance Display */}
              {tradingMode?.mode === 'real' && (
                <div className="p-4 rounded-md bg-primary/5 border border-primary/20">
                  <div className="flex items-center justify-between mb-2">
                    <p className="text-sm font-medium">Real Binance Balance</p>
                    <Button
                      size="sm"
                      variant="ghost"
                      onClick={() => refetchBalance()}
                      data-testid="button-refresh-balance"
                    >
                      <RefreshCw className="h-4 w-4" />
                    </Button>
                  </div>
                  {realBalance ? (
                    <div>
                      <p className="text-2xl font-bold">${realBalance.total} USDT</p>
                      <p className="text-xs text-muted-foreground mt-1">
                        {Object.keys(realBalance.assets).length} assets
                      </p>
                    </div>
                  ) : (
                    <Button
                      size="sm"
                      onClick={() => refetchBalance()}
                      data-testid="button-fetch-balance"
                    >
                      Fetch Balance
                    </Button>
                  )}
                </div>
              )}

              {/* Mode Toggle Buttons */}
              <div className="grid grid-cols-2 gap-4">
                <Button
                  variant={tradingMode?.mode === 'paper' ? 'default' : 'outline'}
                  onClick={() => switchModeMutation.mutate('paper')}
                  disabled={switchModeMutation.isPending || tradingMode?.mode === 'paper'}
                  data-testid="button-switch-paper"
                  className="w-full"
                >
                  {switchModeMutation.isPending ? (
                    <Loader2 className="h-4 w-4 animate-spin mr-2" />
                  ) : null}
                  ðŸ“ Paper Trading
                </Button>
                <Button
                  variant={tradingMode?.mode === 'real' ? 'destructive' : 'outline'}
                  onClick={() => setShowRealModeDialog(true)}
                  disabled={switchModeMutation.isPending || tradingMode?.mode === 'real'}
                  data-testid="button-switch-real"
                  className="w-full"
                >
                  {switchModeMutation.isPending ? (
                    <Loader2 className="h-4 w-4 animate-spin mr-2" />
                  ) : null}
                  ðŸ”´ Real Trading
                </Button>
              </div>

              {/* Safety Warning */}
              <div className="p-4 rounded-md bg-yellow-500/10 border border-yellow-500/20">
                <div className="flex gap-2">
                  <AlertTriangle className="h-5 w-5 text-yellow-600 dark:text-yellow-500 flex-shrink-0 mt-0.5" />
                  <div>
                    <p className="text-sm font-medium text-yellow-600 dark:text-yellow-500">
                      {t('settings.tradingWarning') || 'Trading Risk Warning'}
                    </p>
                    <p className="text-sm text-muted-foreground mt-1">
                      {t('settings.tradingWarningDesc') || 'Real trading mode will execute actual trades on Binance using your account funds. Only use real mode if you understand the risks and have tested your strategy in paper mode first.'}
                    </p>
                  </div>
                </div>
              </div>
            </div>
          </Card>
        )}

        {/* Real Mode Confirmation Dialog */}
        <AlertDialog open={showRealModeDialog} onOpenChange={setShowRealModeDialog}>
          <AlertDialogContent>
            <AlertDialogHeader>
              <AlertDialogTitle className="flex items-center gap-2">
                <AlertTriangle className="h-5 w-5 text-destructive" />
                {t('settings.confirmRealMode') || 'Confirm Real Trading Mode'}
              </AlertDialogTitle>
              <AlertDialogDescription className="space-y-3 text-left">
                <p className="font-medium text-foreground">
                  {t('settings.realModeWarning1') || 'You are about to enable REAL MONEY trading. This means:'}
                </p>
                <ul className="list-disc list-inside space-y-1 text-sm">
                  <li>{t('settings.realModeWarning2') || 'All trades will execute on the live Binance market'}</li>
                  <li>{t('settings.realModeWarning3') || 'Real funds from your Binance account will be used'}</li>
                  <li>{t('settings.realModeWarning4') || 'You can lose money based on market conditions'}</li>
                  <li>{t('settings.realModeWarning5') || 'Trading fees will apply to all transactions'}</li>
                </ul>
                <p className="font-medium text-destructive">
                  {t('settings.realModeWarning6') || 'âš ï¸ Only proceed if you have tested your strategy thoroughly in paper mode and understand the risks.'}
                </p>
              </AlertDialogDescription>
            </AlertDialogHeader>
            <AlertDialogFooter>
              <AlertDialogCancel data-testid="button-cancel-real-mode">
                {t('common.cancel') || 'Cancel'}
              </AlertDialogCancel>
              <AlertDialogAction
                className="bg-destructive hover:bg-destructive/90"
                onClick={() => {
                  switchModeMutation.mutate('real');
                  setShowRealModeDialog(false);
                }}
                data-testid="button-confirm-real-mode"
              >
                {t('settings.confirmRealModeButton') || 'I Understand, Enable Real Mode'}
              </AlertDialogAction>
            </AlertDialogFooter>
          </AlertDialogContent>
        </AlertDialog>

        {/* Information Card */}
        <Card className="p-6" data-testid="card-api-info">
          <h3 className="font-semibold mb-3">{t('settings.howTo')}</h3>
          <ol className="space-y-2 text-sm text-muted-foreground list-decimal list-inside">
            <li>{t('settings.step1')}</li>
            <li>{t('settings.step2')}</li>
            <li>{t('settings.step3')}</li>
            <li>{t('settings.step4')}</li>
            <li>{t('settings.step5')}</li>
            <li>{t('settings.step6')}</li>
            <li>{t('settings.step7')}</li>
          </ol>

          <div className="mt-4 p-4 rounded-md bg-yellow-500/10 border border-yellow-500/20">
            <p className="text-sm font-medium text-yellow-600 dark:text-yellow-500">
              âš ï¸ {t('settings.securityWarning')}
            </p>
            <p className="text-sm text-muted-foreground mt-1">
              {t('settings.securityNote')}
            </p>
          </div>
        </Card>
      </main>
    </div>
  );
}

server/services/risk-management.ts.content:
import type { ChartDataPoint, StrategyType } from "@shared/schema";

export interface RiskMetrics {
  volatility: number; // ATR-based volatility percentage
  recommendedPositionSize: number; // As percentage of portfolio
  maxDrawdown: number; // Maximum acceptable loss
  currentRiskLevel: 'low' | 'medium' | 'high' | 'extreme';
  dailyLossLimit: number; // Daily loss limit in currency
  circuitBreakerActive: boolean;
}

export interface CorrelationMatrix {
  symbols: string[];
  correlations: number[][]; // Correlation coefficients between symbols
}

export class RiskManagementService {
  private readonly ATR_PERIOD = 14; // Standard ATR period
  private readonly MAX_POSITION_PERCENT = 0.25; // Max 25% of portfolio per position
  private readonly BASE_RISK_PERCENT = 0.02; // Base 2% risk per trade
  private readonly VOLATILITY_THRESHOLD_HIGH = 5.0; // 5% ATR is high volatility
  private readonly VOLATILITY_THRESHOLD_EXTREME = 10.0; // 10% ATR is extreme
  private readonly DAILY_LOSS_LIMIT_PERCENT = 0.05; // 5% daily loss limit

  private dailyLosses = new Map<string, number>(); // Track daily losses by date
  private circuitBreakerActive = false;
  private lastCircuitBreakerCheck = Date.now();

  /**
   * Calculate Average True Range (ATR) for volatility measurement
   */
  calculateATR(priceHistory: ChartDataPoint[]): number {
    if (priceHistory.length < this.ATR_PERIOD + 1) {
      return 0;
    }

    const trueRanges: number[] = [];

    for (let i = 1; i < priceHistory.length; i++) {
      const high = priceHistory[i].price;
      const low = priceHistory[i].price * 0.98; // Simulated low (2% below high)
      const prevClose = priceHistory[i - 1].price;

      // True Range = max(high - low, |high - prevClose|, |low - prevClose|)
      const tr = Math.max(
        high - low,
        Math.abs(high - prevClose),
        Math.abs(low - prevClose)
      );
      trueRanges.push(tr);
    }

    // Take last N periods for ATR
    const recentTRs = trueRanges.slice(-this.ATR_PERIOD);
    const atr = recentTRs.reduce((sum, tr) => sum + tr, 0) / recentTRs.length;

    // Return as percentage of current price
    const currentPrice = priceHistory[priceHistory.length - 1].price;
    return (atr / currentPrice) * 100;
  }

  /**
   * Calculate position size based on volatility and risk tolerance
   */
  calculatePositionSize(
    portfolioBalance: number,
    volatility: number,
    strategy: StrategyType
  ): number {
    // Base risk percentage varies by strategy
    const strategyRiskMultiplier = {
      safe: 0.5,
      balanced: 1.0,
      aggressive: 1.5,
    };

    let baseRisk = this.BASE_RISK_PERCENT * strategyRiskMultiplier[strategy];

    // Reduce position size as volatility increases (inverse relationship)
    const volatilityAdjustment = Math.max(0.3, 1 - (volatility / 20));
    const adjustedRisk = baseRisk * volatilityAdjustment;

    // Calculate position size as percentage of portfolio
    let positionPercent = adjustedRisk * 2; // Convert risk to position size

    // Apply maximum position limit
    positionPercent = Math.min(positionPercent, this.MAX_POSITION_PERCENT);

    return positionPercent * portfolioBalance;
  }

  /**
   * Determine current risk level based on volatility
   */
  assessRiskLevel(volatility: number): 'low' | 'medium' | 'high' | 'extreme' {
    if (volatility < 2.0) return 'low';
    if (volatility < this.VOLATILITY_THRESHOLD_HIGH) return 'medium';
    if (volatility < this.VOLATILITY_THRESHOLD_EXTREME) return 'high';
    return 'extreme';
  }

  /**
   * Calculate correlation between two price series
   */
  calculateCorrelation(series1: number[], series2: number[]): number {
    const n = Math.min(series1.length, series2.length);
    if (n < 2) return 0;

    // Use only the most recent N data points
    const x = series1.slice(-n);
    const y = series2.slice(-n);

    // Calculate means
    const meanX = x.reduce((sum, val) => sum + val, 0) / n;
    const meanY = y.reduce((sum, val) => sum + val, 0) / n;

    // Calculate correlation coefficient
    let numerator = 0;
    let denomX = 0;
    let denomY = 0;

    for (let i = 0; i < n; i++) {
      const devX = x[i] - meanX;
      const devY = y[i] - meanY;
      numerator += devX * devY;
      denomX += devX * devX;
      denomY += devY * devY;
    }

    if (denomX === 0 || denomY === 0) return 0;

    return numerator / Math.sqrt(denomX * denomY);
  }

  /**
   * Calculate correlation matrix for multiple symbols
   */
  calculateCorrelationMatrix(
    priceHistories: Map<string, ChartDataPoint[]>
  ): CorrelationMatrix {
    const symbols = Array.from(priceHistories.keys());
    const n = symbols.length;
    const correlations: number[][] = Array(n).fill(0).map(() => Array(n).fill(0));

    // Extract price series for each symbol
    const priceSeries = new Map<string, number[]>();
    priceHistories.forEach((history, symbol) => {
      priceSeries.set(symbol, history.map(point => point.price));
    });

    // Calculate pairwise correlations
    for (let i = 0; i < n; i++) {
      for (let j = 0; j < n; j++) {
        if (i === j) {
          correlations[i][j] = 1.0; // Perfect correlation with self
        } else {
          const series1 = priceSeries.get(symbols[i]) || [];
          const series2 = priceSeries.get(symbols[j]) || [];
          correlations[i][j] = this.calculateCorrelation(series1, series2);
        }
      }
    }

    return { symbols, correlations };
  }

  /**
   * Check if circuit breaker should be activated
   */
  checkCircuitBreaker(
    currentBalance: number,
    initialBalance: number,
    dailyStartBalance: number
  ): boolean {
    const today = new Date().toISOString().split('T')[0];

    // Calculate daily loss
    const dailyLoss = dailyStartBalance - currentBalance;
    const dailyLossPercent = (dailyLoss / dailyStartBalance) * 100;

    // Update daily loss tracking
    this.dailyLosses.set(today, dailyLoss);

    // Calculate daily loss limit
    const dailyLossLimit = dailyStartBalance * this.DAILY_LOSS_LIMIT_PERCENT;

    // Activate circuit breaker if daily loss limit exceeded
    if (dailyLoss >= dailyLossLimit) {
      this.circuitBreakerActive = true;
      console.warn(`ðŸ›‘ Circuit Breaker Activated: Daily loss ${dailyLossPercent}% exceeds ${(this.DAILY_LOSS_LIMIT_PERCENT * 100).toFixed(0)}% limit`);
      return true;
    }

    // Auto-reset circuit breaker at start of new day
    const timeSinceLastCheck = Date.now() - this.lastCircuitBreakerCheck;
    if (timeSinceLastCheck > 24 * 60 * 60 * 1000) { // 24 hours
      this.circuitBreakerActive = false;
      this.lastCircuitBreakerCheck = Date.now();
    }

    return this.circuitBreakerActive;
  }

  /**
   * Calculate comprehensive risk metrics
   */
  calculateRiskMetrics(
    priceHistory: ChartDataPoint[],
    portfolioBalance: number,
    initialBalance: number,
    strategy: StrategyType,
    dailyStartBalance?: number
  ): RiskMetrics {
    // Calculate volatility using ATR
    const volatility = this.calculateATR(priceHistory);

    // Determine risk level
    const currentRiskLevel = this.assessRiskLevel(volatility);

    // Calculate recommended position size
    const recommendedPositionSize = this.calculatePositionSize(
      portfolioBalance,
      volatility,
      strategy
    );

    // Calculate maximum drawdown tolerance
    const maxDrawdown = portfolioBalance * this.BASE_RISK_PERCENT * 3; // 6% for balanced

    // Calculate daily loss limit
    const dailyLossLimit = (dailyStartBalance || portfolioBalance) * this.DAILY_LOSS_LIMIT_PERCENT;

    // Check circuit breaker
    const circuitBreakerActive = this.checkCircuitBreaker(
      portfolioBalance,
      initialBalance,
      dailyStartBalance || portfolioBalance
    );

    return {
      volatility,
      recommendedPositionSize,
      maxDrawdown,
      currentRiskLevel,
      dailyLossLimit,
      circuitBreakerActive,
    };
  }

  /**
   * Reset circuit breaker manually
   */
  resetCircuitBreaker(): void {
    this.circuitBreakerActive = false;
    this.lastCircuitBreakerCheck = Date.now();
    console.log('âœ… Circuit Breaker Reset');
  }

  /**
   * Get circuit breaker status
   */
  isCircuitBreakerActive(): boolean {
    return this.circuitBreakerActive;
  }

  /**
   * Calculate trailing stop price
   */
  calculateTrailingStop(
    entryPrice: number,
    currentPrice: number,
    volatility: number
  ): number {
    // Trailing stop distance adjusts based on volatility
    // Higher volatility = wider stop to avoid premature exits
    const baseStopPercent = 0.03; // 3% base
    const volatilityAdjustment = Math.min(volatility / 100, 0.05); // Add up to 5% for volatility
    const stopDistance = baseStopPercent + volatilityAdjustment;

    // Calculate stop price
    const stopPrice = currentPrice * (1 - stopDistance);

    // Never set stop below entry price for long positions
    return Math.max(stopPrice, entryPrice * 0.95); // At least 5% below entry
  }
}

// Singleton instance
let riskManagementInstance: RiskManagementService | null = null;

export function getRiskManagementService(): RiskManagementService {
  if (!riskManagementInstance) {
    riskManagementInstance = new RiskManagementService();
  }
  return riskManagementInstance;
}

server/services/strategy-ai.ts.content:
import type { StrategyType, PriceData, MarketConditions, StrategyScore, AIDecision } from "@shared/schema";
import { randomUUID } from "crypto";

/**
 * AI Strategy Service
 * Analyzes market conditions and intelligently selects optimal trading strategies
 */
export class StrategyAI {
  private priceHistory: Map<string, PriceData[]> = new Map();
  private readonly HISTORY_SIZE = 50; // Keep last 50 price points per symbol
  private aiDecisionHistory: AIDecision[] = [];

  /**
   * Update price history for market analysis
   */
  public updatePriceData(priceData: PriceData[]) {
    priceData.forEach(data => {
      if (!this.priceHistory.has(data.symbol)) {
        this.priceHistory.set(data.symbol, []);
      }

      const history = this.priceHistory.get(data.symbol)!;
      history.push(data);

      // Keep only recent history
      if (history.length > this.HISTORY_SIZE) {
        history.shift();
      }
    });
  }

  /**
   * Analyze current market conditions
   */
  public analyzeMarketConditions(): MarketConditions {
    // Aggregate metrics across all symbols
    let totalVolatility = 0;
    let totalTrendStrength = 0;
    let totalMomentum = 0;
    let totalVolumeTrend = 0;
    let symbolCount = 0;

    this.priceHistory.forEach((history, symbol) => {
      if (history.length < 20) return; // Need enough data

      const metrics = this.calculateSymbolMetrics(history);
      totalVolatility += metrics.volatility;
      totalTrendStrength += metrics.trendStrength;
      totalMomentum += metrics.momentum;
      totalVolumeTrend += metrics.volumeTrend;
      symbolCount++;
    });

    if (symbolCount === 0) {
      // Default conditions if no data
      return {
        volatility: 50,
        trendStrength: 0,
        momentum: 0,
        volumeTrend: 0,
        riskLevel: 'medium',
      };
    }

    const avgVolatility = totalVolatility / symbolCount;
    const avgTrendStrength = totalTrendStrength / symbolCount;
    const avgMomentum = totalMomentum / symbolCount;
    const avgVolumeTrend = totalVolumeTrend / symbolCount;

    // Determine risk level based on volatility
    let riskLevel: 'low' | 'medium' | 'high';
    if (avgVolatility < 30) riskLevel = 'low';
    else if (avgVolatility < 60) riskLevel = 'medium';
    else riskLevel = 'high';

    return {
      volatility: avgVolatility,
      trendStrength: avgTrendStrength,
      momentum: avgMomentum,
      volumeTrend: avgVolumeTrend,
      riskLevel,
    };
  }

  /**
   * Calculate metrics for a single symbol
   */
  private calculateSymbolMetrics(history: PriceData[]) {
    const prices = history.map(d => d.price);
    const volumes = history.map(d => d.volume24h);

    // Volatility: Standard deviation of price changes as % of mean price
    const priceChanges = [];
    for (let i = 1; i < prices.length; i++) {
      priceChanges.push(((prices[i] - prices[i-1]) / prices[i-1]) * 100);
    }
    const avgChange = priceChanges.reduce((a, b) => a + b, 0) / priceChanges.length;
    const variance = priceChanges.reduce((sum, change) => sum + Math.pow(change - avgChange, 2), 0) / priceChanges.length;
    const volatility = Math.min(Math.sqrt(variance) * 10, 100); // Scale to 0-100

    // Trend Strength: EMA20 slope and position relative to price
    const ema20 = this.calculateEMA(prices, 20);
    const emaSlope = ((ema20[ema20.length - 1] - ema20[ema20.length - 10]) / ema20[ema20.length - 10]) * 1000;
    const trendStrength = Math.max(-100, Math.min(100, emaSlope));

    // Momentum: Rate of change over last 10 periods
    const momentum10 = prices.length >= 10
      ? ((prices[prices.length - 1] - prices[prices.length - 10]) / prices[prices.length - 10]) * 100
      : 0;
    const momentum = Math.max(-100, Math.min(100, momentum10 * 2));

    // Volume Trend: Compare recent volume to historical average
    const recentVolume = volumes.slice(-5).reduce((a, b) => a + b, 0) / 5;
    const historicalVolume = volumes.slice(0, -5).reduce((a, b) => a + b, 0) / (volumes.length - 5);
    const volumeChange = ((recentVolume - historicalVolume) / historicalVolume) * 100;
    const volumeTrend = Math.max(-100, Math.min(100, volumeChange));

    return { volatility, trendStrength, momentum, volumeTrend };
  }

  /**
   * Calculate Exponential Moving Average
   */
  private calculateEMA(prices: number[], period: number): number[] {
    const ema: number[] = [];
    const multiplier = 2 / (period + 1);

    // Start with SMA
    let sum = 0;
    for (let i = 0; i < Math.min(period, prices.length); i++) {
      sum += prices[i];
    }
    ema.push(sum / Math.min(period, prices.length));

    // Calculate EMA for remaining values
    for (let i = period; i < prices.length; i++) {
      const value = (prices[i] - ema[ema.length - 1]) * multiplier + ema[ema.length - 1];
      ema.push(value);
    }

    return ema;
  }

  /**
   * Score each strategy based on market conditions
   */
  public scoreStrategies(conditions: MarketConditions): StrategyScore[] {
    const scores: StrategyScore[] = [];

    // Score SAFE strategy
    const safeReasons: string[] = [];
    let safeScore = 50; // Base score

    if (conditions.volatility < 40) {
      safeScore += 30;
      safeReasons.push('Low volatility favors conservative approach');
    }
    if (conditions.riskLevel === 'low') {
      safeScore += 20;
      safeReasons.push('Low risk environment suits safe trading');
    }
    if (Math.abs(conditions.trendStrength) < 30) {
      safeScore += 15;
      safeReasons.push('Weak trends reduce profit opportunities');
    }
    if (conditions.volatility > 70) {
      safeScore -= 30;
      safeReasons.push('High volatility increases risk');
    }

    scores.push({
      strategy: 'safe',
      score: Math.max(0, Math.min(100, safeScore)),
      reasons: safeReasons.length > 0 ? safeReasons : ['Standard safe strategy conditions'],
      confidence: this.calculateConfidence(conditions, safeScore),
    });

    // Score BALANCED strategy
    const balancedReasons: string[] = [];
    let balancedScore = 50;

    if (conditions.volatility >= 30 && conditions.volatility <= 60) {
      balancedScore += 35;
      balancedReasons.push('Moderate volatility optimal for balanced approach');
    }
    if (conditions.riskLevel === 'medium') {
      balancedScore += 25;
      balancedReasons.push('Medium risk environment ideal');
    }
    if (Math.abs(conditions.momentum) < 50) {
      balancedScore += 15;
      balancedReasons.push('Moderate momentum supports balanced trading');
    }
    if (conditions.volumeTrend > 10) {
      balancedScore += 10;
      balancedReasons.push('Increasing volume provides liquidity');
    }

    scores.push({
      strategy: 'balanced',
      score: Math.max(0, Math.min(100, balancedScore)),
      reasons: balancedReasons.length > 0 ? balancedReasons : ['Standard balanced strategy conditions'],
      confidence: this.calculateConfidence(conditions, balancedScore),
    });

    // Score AGGRESSIVE strategy
    const aggressiveReasons: string[] = [];
    let aggressiveScore = 50;

    if (conditions.volatility > 60) {
      aggressiveScore += 35;
      aggressiveReasons.push('High volatility creates profit opportunities');
    }
    if (Math.abs(conditions.trendStrength) > 50) {
      aggressiveScore += 30;
      aggressiveReasons.push('Strong trend enables aggressive positioning');
    }
    if (Math.abs(conditions.momentum) > 60) {
      aggressiveScore += 20;
      aggressiveReasons.push('Strong momentum supports aggressive trades');
    }
    if (conditions.volumeTrend > 30) {
      aggressiveScore += 10;
      aggressiveReasons.push('High volume surge indicates major movement');
    }
    if (conditions.volatility < 30) {
      aggressiveScore -= 30;
      aggressiveReasons.push('Low volatility limits profit potential');
    }

    scores.push({
      strategy: 'aggressive',
      score: Math.max(0, Math.min(100, aggressiveScore)),
      reasons: aggressiveReasons.length > 0 ? aggressiveReasons : ['Standard aggressive strategy conditions'],
      confidence: this.calculateConfidence(conditions, aggressiveScore),
    });

    return scores.sort((a, b) => b.score - a.score);
  }

  /**
   * Calculate confidence level for a strategy score
   */
  private calculateConfidence(conditions: MarketConditions, score: number): number {
    // Base confidence on data quality and market stability
    let confidence = 70; // Base confidence

    // More data = more confidence
    const avgDataPoints = Array.from(this.priceHistory.values())
      .reduce((sum, history) => sum + history.length, 0) / this.priceHistory.size;

    if (avgDataPoints > 40) confidence += 15;
    else if (avgDataPoints < 20) confidence -= 20;

    // Clear market conditions = higher confidence
    if (conditions.volatility < 30 || conditions.volatility > 70) confidence += 10;
    if (Math.abs(conditions.trendStrength) > 50) confidence += 10;

    // Extreme score deviations = lower confidence
    const scoreDiff = Math.abs(score - 50);
    if (scoreDiff > 40) confidence += 5; // Very clear signal
    else if (scoreDiff < 10) confidence -= 15; // Uncertain

    return Math.max(0, Math.min(100, confidence));
  }

  /**
   * Select best strategy based on AI analysis
   */
  public selectBestStrategy(currentStrategy: StrategyType): AIDecision {
    const conditions = this.analyzeMarketConditions();
    const strategyScores = this.scoreStrategies(conditions);
    const bestStrategy = strategyScores[0];

    // Build reasoning
    const reasoning = this.buildReasoning(conditions, strategyScores, bestStrategy);

    // Calculate expected win rate based on strategy and conditions
    const expectedWinRate = this.calculateExpectedWinRate(bestStrategy.strategy, conditions);

    const decision: AIDecision = {
      id: randomUUID(),
      timestamp: Date.now(),
      marketConditions: conditions,
      strategyScores,
      selectedStrategy: bestStrategy.strategy,
      previousStrategy: currentStrategy,
      reasoning,
      confidence: bestStrategy.confidence,
      expectedWinRate,
    };

    // Store decision history
    this.aiDecisionHistory.push(decision);
    if (this.aiDecisionHistory.length > 100) {
      this.aiDecisionHistory.shift();
    }

    return decision;
  }

  /**
   * Build human-readable reasoning for strategy selection
   */
  private buildReasoning(
    conditions: MarketConditions,
    scores: StrategyScore[],
    selectedScore: StrategyScore
  ): string {
    const parts: string[] = [];

    // Market overview
    parts.push(`Market analysis shows ${conditions.volatility.toFixed(0)}% volatility with ${conditions.riskLevel} risk level.`);

    if (Math.abs(conditions.trendStrength) > 50) {
      parts.push(`Strong ${conditions.trendStrength > 0 ? 'upward' : 'downward'} trend detected.`);
    } else {
      parts.push(`Market showing weak trending behavior.`);
    }

    // Strategy rationale
    parts.push(`${selectedScore.strategy.toUpperCase()} strategy scored highest (${selectedScore.score.toFixed(0)}/100) because:`);
    parts.push(selectedScore.reasons.join('; '));

    // Comparison
    const otherScores = scores.filter(s => s.strategy !== selectedScore.strategy);
    if (otherScores.length > 0) {
      parts.push(`Alternative strategies: ${otherScores.map(s => `${s.strategy} (${s.score.toFixed(0)})`).join(', ')}.`);
    }

    return parts.join(' ');
  }

  /**
   * Calculate expected win rate for strategy under current conditions
   */
  private calculateExpectedWinRate(strategy: StrategyType, conditions: MarketConditions): number {
    // Base win rates
    const baseRates = {
      safe: 70,
      balanced: 60,
      aggressive: 50,
    };

    let winRate = baseRates[strategy];

    // Adjust based on conditions matching strategy
    if (strategy === 'safe') {
      if (conditions.volatility < 40) winRate += 10;
      if (conditions.riskLevel === 'low') winRate += 5;
      if (conditions.volatility > 70) winRate -= 15;
    } else if (strategy === 'balanced') {
      if (conditions.volatility >= 30 && conditions.volatility <= 60) winRate += 12;
      if (conditions.riskLevel === 'medium') winRate += 8;
    } else if (strategy === 'aggressive') {
      if (conditions.volatility > 60) winRate += 10;
      if (Math.abs(conditions.trendStrength) > 50) winRate += 8;
      if (conditions.volatility < 30) winRate -= 15;
    }

    return Math.max(30, Math.min(85, winRate));
  }

  /**
   * Get recent AI decision history
   */
  public getDecisionHistory(limit: number = 20): AIDecision[] {
    return this.aiDecisionHistory.slice(-limit);
  }

  /**
   * Clear decision history (for testing/reset)
   */
  public clearHistory() {
    this.aiDecisionHistory = [];
    this.priceHistory.clear();
  }
}

// Singleton instance
export const strategyAI = new StrategyAI();

